// Simple round-robin teacher selection (no Redis needed)
const teachers = ['teacher_a', 'teacher_b', 'teacher_c', 'teacher_d', 'teacher_e'];
const lastTeacher = $input.item.json.lastTeacher || 'teacher_e';
const currentIndex = teachers.indexOf(lastTeacher);
const nextIndex = (currentIndex + 1) % teachers.length;
const selectedTeacher = teachers[nextIndex];

// Extract message from webhook body - try multiple locations
let message = '';
const inputData = $input.item.json;

// Try different ways to get the message
if (inputData.body) {
  // Body might be an object or string
  if (typeof inputData.body === 'object') {
    message = inputData.body.message || '';
  } else if (typeof inputData.body === 'string') {
    try {
      const parsed = JSON.parse(inputData.body);
      message = parsed.message || '';
    } catch (e) {
      message = inputData.body;
    }
  }
}

// Try direct message field
if (!message && inputData.message) {
  message = inputData.message;
}

// Try query parameters
if (!message && inputData.query && inputData.query.message) {
  message = inputData.query.message;
}

// Fallback: use body as-is if it's a string
if (!message && inputData.body && typeof inputData.body === 'string') {
  message = inputData.body;
}

// If still no message, use a default
if (!message) {
  message = 'Hello, how can I help you?';
}

return {
  json: {
    selectedTeacher: selectedTeacher,
    lastTeacher: selectedTeacher,
    message: message
  }
};

