  json: {
    text: responseText || 'Hello! I am ready to help.',
    teacher: 'teacher_e'
  }
};

// Select the response from the teacher that was selected by round-robin
const allResponses = $input.all();
let selectedTeacher = '';

try {
  // Get selectedTeacher from execution context
  selectedTeacher = $('Select Teacher (Round-Robin)').first().json.selectedTeacher;
} catch (e) {
  // Fallback: use round-robin logic here
  const teachers = ['teacher_a', 'teacher_b', 'teacher_c', 'teacher_d', 'teacher_e'];
  // Simple round-robin: cycle through teachers
  const index = Math.floor(Date.now() / 1000) % teachers.length;
  selectedTeacher = teachers[index];
}

// Find the response from the selected teacher
const selectedResponse = allResponses.find(item => item.json.teacher === selectedTeacher);

if (selectedResponse) {
  return selectedResponse.json;
} else {
  // Fallback to first response if selected teacher not found
  return allResponses[0].json;
}

// Prepare data for LongCat-Video-Avatar API
const ttsResponse = $input.item.json;

// Get selectedTeacher and responseText from execution context
let selectedTeacher = 'teacher_a';
let responseText = '';

try {
  // Get from Select Response by Teacher node (previous node in chain)
  const selectResponseData = $('Select Response by Teacher').first().json;
  responseText = selectResponseData.text || '';
  // Get selectedTeacher from Select Teacher node
  selectedTeacher = $('Select Teacher (Round-Robin)').first().json.selectedTeacher || 'teacher_a';
} catch (e) {
  // Fallback: try to get from input
  responseText = ttsResponse.text || '';
  selectedTeacher = 'teacher_a';
}

let audioUrl = '';

// Extract audio URL from TTS response (try multiple formats)
if (ttsResponse.audio_url) {
  audioUrl = ttsResponse.audio_url;
} else if (ttsResponse.url) {
  audioUrl = ttsResponse.url;
} else if (ttsResponse.audio) {
  audioUrl = ttsResponse.audio;
} else if (ttsResponse.audio_base64) {
  // If TTS returns base64, we need to save it and create a URL
  // For now, return an error - TTS service should be updated to return audio_url
  throw new Error('TTS service returned audio_base64 but LongCat-Video needs audio_url. Please update TTS service to save files and return URLs.');
}

// Validate we have an audio URL
if (!audioUrl || audioUrl === '') {
  throw new Error('No audio URL found in TTS response. TTS service must return audio_url field.');
}

// Teacher-specific text prompts for LongCat-Video
const teacherPrompts = {
  'teacher_a': 'A warm and approachable educator with expertise in making complex topics relatable. Speaking naturally and conversationally.',
  'teacher_b': 'A technical expert with deep knowledge in advanced topics. Speaking precisely and analytically.',
  'teacher_c': 'An enthusiastic and knowledgeable educator with extensive teaching experience. Speaking clearly and engagingly.',
  'teacher_d': 'An innovative and creative educator who makes learning fun. Speaking energetically and creatively.',
  'teacher_e': 'A knowledgeable and adaptable educator. Speaking clearly and supportively.'
};

const textPrompt = teacherPrompts[selectedTeacher] || 'A person speaking naturally';

return {
  json: {
    audio_url: audioUrl,
    avatar_id: String(selectedTeacher), // Ensure it's a string
    text_prompt: textPrompt,
    text: responseText
  }
};

// Format response for frontend
const longcatResponse = $input.item.json;

// Get text and teacher from execution context
let text = '';
let teacher = 'teacher_a';

try {
  // Get from Select Response by Teacher node (previous node in chain)
  const selectResponseData = $('Select Response by Teacher').first().json;
  text = selectResponseData.text || '';
  teacher = selectResponseData.teacher || 'teacher_a';
  // Also try to get selectedTeacher from Select Teacher node
  const selectedTeacher = $('Select Teacher (Round-Robin)').first().json.selectedTeacher;
  if (selectedTeacher) {
    teacher = selectedTeacher;
  }
} catch (e) {
  // Fallback
  text = 'Hello! I am ready to help.';
  teacher = 'teacher_a';
}

// LongCat-Video returns video_url, video_path, job_id, status
// If status is 'processing', we return the job_id for polling
// If status is 'completed', we return the video_url
let videoUrl = '';
let jobId = '';

if (longcatResponse.status === 'processing' || longcatResponse.status === 'completed') {
  // Construct full video URL if we have a job_id
  if (longcatResponse.job_id) {
    jobId = longcatResponse.job_id;
    // Video URL format: http://localhost:8003/video/{job_id}
    videoUrl = `http://localhost:8003/video/${jobId}`;
  } else if (longcatResponse.video_url) {
    videoUrl = longcatResponse.video_url;
  } else if (longcatResponse.video_path) {
    // If we only have a path, construct URL
    videoUrl = longcatResponse.video_path;
  }
}

return {
  json: {
    response: text,
    teacher: teacher,
    video_url: videoUrl,
    job_id: jobId || longcatResponse.job_id || '',
    status: longcatResponse.status || 'unknown'
  }
};
