let responseText = '';

if (llmResponse.response) {
  responseText = llmResponse.response;
} else if (typeof llmResponse === 'string') {
  responseText = llmResponse;
} else if (llmResponse.text) {
  responseText = llmResponse.text;
}

// Determine which teacher this is based on node name
const nodeName = $node.name;
let teacherId = 'teacher_a';
if (nodeName.includes('Teacher B')) teacherId = 'teacher_b';
else if (nodeName.includes('Teacher C')) teacherId = 'teacher_c';
else if (nodeName.includes('Teacher D')) teacherId = 'teacher_d';
else if (nodeName.includes('Teacher E')) teacherId = 'teacher_e';

return {
  json: {
    text: responseText || 'Hello! I am ready to help.',
    teacher: teacherId,
    message: $('Select Teacher (Round-Robin)').item.json.message,
    selectedTeacher: $('Select Teacher (Round-Robin)').item.json.selectedTeacher
  }
};

