parameters.jsCode:
// Extract job payload
const inputData = $input.item.json;
let body = inputData.body || inputData;
if (typeof body === 'string') {
  try {
    body = JSON.parse(body);
  } catch (e) {
    body = {};
  }
}

const sessionId = body.sessionId || '';
const teacher = body.teacher || '';
const coTeacher = body.coTeacher || '';
const role = body.role || 'renderer';
const sectionPayload = body.sectionPayload || {};
const language = body.language || 'English';
const turn = body.turn || 0;

if (!sessionId || !teacher) {
  throw new Error('Missing sessionId or teacher');
}

return {
  json: {
    sessionId: sessionId,
    teacher: teacher,
    coTeacher: coTeacher,
    role: role,
    sectionPayload: sectionPayload,
    language: language,
    turn: turn
  }
};

