parameters.jsCode:
// Extract and normalize LLM response
const llmResponse = $input.item.json;
let responseText = '';

// Handle Ollama API response format
if (llmResponse.response) {
  responseText = llmResponse.response.trim();
} else if (llmResponse.text) {
  responseText = llmResponse.text.trim();
} else if (typeof llmResponse === 'string') {
  responseText = llmResponse.trim();
} else if (llmResponse.message && llmResponse.message.content) {
  responseText = llmResponse.message.content.trim();
} else if (Array.isArray(llmResponse) && llmResponse.length > 0) {
  // Handle streaming response array
  responseText = llmResponse.map(item => item.response || item.text || '').join('').trim();
} else {
  // Fallback: try to extract from any text field
  responseText = JSON.stringify(llmResponse).substring(0, 200);
}

// Safety: ensure minimum length
if (!responseText || responseText.length < 10) {
  responseText = 'Let me continue explaining this section.';
}

// Trim to reasonable length (target ~8-12 seconds = ~20-30 words)
const words = responseText.split(/\s+/).filter(w => w.length > 0);
if (words.length > 35) {
  responseText = words.slice(0, 35).join(' ') + '...';
}

return {
  json: {
    text: responseText,
    teacher: $('Extract Payload').item.json.teacher,
    sessionId: $('Extract Payload').item.json.sessionId,
    turn: $('Extract Payload').item.json.turn
  }
};

// Map language to TTS voice
const language = $('Extract Payload').item.json.language || 'English';
const text = $('Extract Response').item.json.text;

// Language to voice mapping (Piper TTS voices)
const languageVoices = {
  'English': 'en_US-lessac-medium',
  'Spanish': 'es_ES-sharvard-medium',
  'French': 'fr_FR-siwis-medium',
  'German': 'de_DE-thorsten-medium',
  'Chinese (Simplified)': 'zh_CN-huayan-medium',
  'Japanese': 'ja_JP-natsumi-medium',
  'Korean': 'ko_KR-kyungha-medium',
  'Portuguese': 'pt_BR-faber-medium',
  'Italian': 'it_IT-riccardo-medium',
  'Russian': 'ru_RU-ruslan-medium'
};

const voice = languageVoices[language] || 'en_US-lessac-medium';

return {
  json: {
    text: text,
    voice: voice,
    language: language
  }
};

// Prepare data for LongCat-Video-Avatar API
const ttsResponse = $input.item.json;
const teacher = $('Extract Response').item.json.teacher;
const text = $('Extract Response').item.json.text;
const language = $('Extract Payload').item.json.language || 'English';

let audioUrl = '';

// Extract audio URL from TTS response (try multiple possible fields)
if (ttsResponse.audio_url) {
  audioUrl = ttsResponse.audio_url;
} else if (ttsResponse.url) {
  audioUrl = ttsResponse.url;
} else if (ttsResponse.audio) {
  audioUrl = ttsResponse.audio;
} else if (ttsResponse.file_url) {
  audioUrl = ttsResponse.file_url;
} else if (typeof ttsResponse === 'string') {
  audioUrl = ttsResponse;
} else {
  throw new Error(`No audio URL found in TTS response. Response keys: ${Object.keys(ttsResponse).join(', ')}`);
}

// Ensure audioUrl is a full URL, not just a path
if (audioUrl && !audioUrl.startsWith('http')) {
  audioUrl = `http://localhost:8001${audioUrl.startsWith('/') ? '' : '/'}${audioUrl}`;
}

// Teacher-specific text prompts
const teacherPrompts = {
  'teacher_a': 'A warm and approachable educator speaking naturally.',
  'teacher_b': 'A technical expert speaking precisely.',
  'teacher_c': 'An enthusiastic educator speaking clearly.',
  'teacher_d': 'An innovative educator speaking energetically.',
  'teacher_e': 'A knowledgeable educator speaking supportively.'
};

const textPrompt = teacherPrompts[teacher] || 'A person speaking naturally';

return {
  json: {
    audio_url: audioUrl,
    avatar_id: String(teacher),
    text_prompt: textPrompt,
    text: text,
    language: language,
    sessionId: $('Extract Response').item.json.sessionId,
    turn: $('Extract Response').item.json.turn
  }
};

// Format clip data for Coordinator
const videoResponse = $input.item.json;
const teacher = $('Extract Response').item.json.teacher;
const text = $('Extract Response').item.json.text;
const sessionId = $('Extract Response').item.json.sessionId;
const turn = $('Extract Response').item.json.turn;

// Generate clip ID
const clipId = `clip-${sessionId}-${teacher}-${turn}-${Date.now()}`;

// Extract video URL
let videoUrl = '';
let jobId = '';
let status = 'processing';

if (videoResponse) {
  status = videoResponse.status || 'processing';
  jobId = videoResponse.job_id || '';

  if (jobId) {
    videoUrl = `http://localhost:8003/video/${jobId}`;
  } else if (videoResponse.video_url) {
    videoUrl = videoResponse.video_url;
  }
}

// Construct clip object
const clip = {
  clipId: clipId,
  text: text,
  audioUrl: $('TTS Generate').item.json.audio_url || '',
  videoUrl: videoUrl,
  jobId: jobId,
  durationMs: Math.ceil(text.split(' ').length * 0.5 * 1000), // Rough estimate
  status: status,
  sectionId: $('Extract Payload').item.json.sectionPayload?.sectionId || null,
  turn: turn
};

return {
  json: {
    sessionId: sessionId,
    teacher: teacher,
    clip: clip
  }
};

// Prepare clip-ready payload
const input = $input.item.json;

// Validate required fields
if (!input.sessionId || !input.teacher || !input.clip) {
  throw new Error(`Missing required fields: sessionId=${!!input.sessionId}, teacher=${!!input.teacher}, clip=${!!input.clip}`);
}

return {
  json: {
    sessionId: input.sessionId,
    teacher: input.teacher,
    clip: input.clip
  }
};

// Error handler - format error response
const error = $input.first()?.error || { message: 'Unknown error' };
const sessionId = $('Extract Payload')?.first()?.json?.sessionId || 'unknown';

return {
  json: {
    error: true,
    message: error.message || 'Workflow execution failed',
    sessionId: sessionId,
    node: error.node || 'unknown',
    timestamp: new Date().toISOString()
  }
};
